import React, { Component } from 'react';
import PropTypes from 'prop-types';
// MUI staff
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import withStyles from '@material-ui/core/styles/withStyles';

const styles = {

    image: {
        margin: '20px auto 20px auto'
    }

};
class FlashCard extends Component {

    state = {
        isFront: true,
        editMode: false,
        front: "",
        back: ""
    }

    componentDidMount(){

        if(this.props.editMode){
            this.setState({
                editMode: true,
                front: this.props.card.front,
                back: this.props.card.back
            })

        }
    }

    handleFChange = (e) => {

        // this.setState({
        //     front: e.target.value
        // })
        this.props.card.front = e.target.value;

    }
    handleBChange = (e) => {

        this.setState({
            back: e.target.value
        })
        this.props.card.back = e.target.value;
    }

    handleClick = (e) => {

        this.setState({
            isFront: !this.state.isFront
        })
        if(e.target.innerHTML == "Again"){
            console.log("AGAIN");
        }else if(e.target.innerHTML == "Good"){
            console.log("GOOD");
        }else if(e.target.innerHTML == "Easy"){
            console.log("EASY");
        }

    }

    render() {
        const { classes } = this.props;
        return (

            <div>

                {
                    this.state.editMode && (
                        <Card className="card-front">
                            <div className="card-front-inner">
                            <input onChange={this.handleFChange} type="text" value={this.props.card.front} name="mnumber" placeholder="Question"/>

                            <hr />

                            <input onChange={this.handleBChange} type="text" value={this.props.card.back} name="mnumber" placeholder="Answer"/>


                            </div>
                            <Button onClick={this.handleSave} variant="contained" color="primary" className="card-button-f">
                                Save
                            </Button>
                            <Button onClick={this.handleDelete} variant="contained" color="secondary" className="card-button-f">
                                Delete
                            </Button>


                        </Card>

                    )
                }



                {
                    this.state.isFront && !this.state.editMode && (
                        <Card className="card-front">
                            <div className="card-front-inner">

                            {this.props.card.front}
                            <hr />

                            </div>
                            <Button onClick={this.handleClick} variant="contained" color="primary" className="card-button-f">
                                Show Answer
                            </Button>



                        </Card>

                    )
                }

                {
                    !this.state.isFront && !this.state.editMode && (
                        <Card className="card-front">
                             <div className="card-front-inner">
                             {this.props.card.front}
                                <hr/>
                                {this.props.card.back}
                            <br/>

                            </div>

                            <Button onClick={this.handleClick} value="tata" variant="contained" color="primary" className="card-button-b">
                                Again
                            </Button>
                            <Button onClick={this.handleClick} variant="contained" color="primary" className="card-button-b">
                                Good
                            </Button>
                            <Button onClick={this.handleClick} variant="contained" color="primary" className="card-button-b">
                                Easy
                            </Button>
                        </Card>

                    ) 
                }
                <br/>
          </div>
        )
    }
}

FlashCard.propTypes = {

    classes: PropTypes.object.isRequired

}

export default withStyles(styles)(FlashCard);
