import React, { Component } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';

// COMPONETS

import FlashCard from './FlashCard';
import Module from './Module';

// MATERIAL UI

import withStyles from '@material-ui/core/styles/withStyles';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import { InputAdornment } from '@material-ui/core';

const styles = {
  typography: {
    useNextVariants: true
  },
  form: {
    textAlign: 'center'
  },
  image: {
    margin: '20px auto 20px auto'
  },
  pageTitle: {
    margin: '100px auto 10px auto'
  },
  pageTitle2: {
    margin: '30px auto 10px auto'
  },
  textField: {
    margin: '10px auto 10px auto'
  },
  button: {
    marginTop: 20,
    position: 'relative'
  },
}



class Content extends Component {

  constructor(){
    super();
    this.state = {
      cards: [],
      name: "",
      modules: [],
      index: 0,
      loaded: false,
      isSelecting: false,
      editMode: false
    }

};

  componentDidMount(){

    if(this.props.admin){
      const userId  = this.props.user.id;
      console.log("Id Admin MuserId ", userId);
      axios.get(`/module/user/${userId}`)
        .then(res => {
          this.setState({
            modules: res.data
          })

          if(this.state.modules.length > 0){
            this.setState({
              loaded: true
            })
          }
          const cardIds = this.state.modules[0].cardIds;
          axios.post("/card/cardIds", {cardIds})
          .then(res => {
            console.log("cards fetched", res.data);
            this.setState({
              cards: res.data
            })

          }).catch((err) => {
            console.log("Card Error", err);
          });


        }).catch((err) => {
          console.log("Module Error", err);
        })

    }else{

      console.log("Home === not admin");

      axios.get(`/module`)
      .then(res => {
        this.setState({
          modules: res.data
        })
        console.log("state: <<<<<<<<<<< ", this.state);
        if(this.state.modules.length > 0){
          this.setState({
            loaded: true
          })
        }
        const cardIds = this.state.modules[0].cardIds;
        axios.post("/card/cardIds", {cardIds})
        .then(res => {
          console.log("cards fetched", res.data);
          this.setState({
            cards: res.data
          })

        }).catch((err) => {
          console.log("Card Error", err);
        });

      }).catch((err) => {
        console.log("Module Error", err);
      })
    }

  }

  getModules = () => {

    if(this.props.admin){
      const userId  = this.props.user.id;
      console.log("Admin MuserId ", userId);
      axios.get(`/module/user/${userId}`)
        .then(res => {
          this.setState({
            modules: res.data
          })
        }).catch((err) => {
          console.log("Module Error", err);
        })
    }else{
      console.log("not admin");
      axios.get(`/module`)
      .then(res => {
        this.setState({
          modules: res.data
        })
      }).catch((err) => {
        console.log("Module Error", err);
      })

    }

  }

  getCards = (index) => {

    const cardIds = this.state.modules[index].cardIds;
      axios.post("/card/cardIds", {cardIds})
      .then(res => {
        console.log("cards fetched", res.data);
        this.setState({
          cards: res.data
        })

      }).catch((err) => {
        console.log("Card Error", err);
      });

  }

  handleForwardClick = () => {

    this.setState({
      index: (this.state.index + 1) % this.state.modules.length
    });
    this.getCards(this.state.index);

  }
  modulo(n, m){
    return ((n % m) + m) % m ;
  }
  handleBackwardClick = () => {

    this.setState({
      index: this.modulo((this.state.index - 1), this.state.modules.length)
    });
    this.getCards(this.state.index);

  }

  handleSelectModule =  () => {
    this.setState({
      isSelecting: !this.state.isSelecting
    })
  }

  getCards = (index) => {

    if(this.state.modules.length > index){

      const cardIds = this.state.modules[index].cardIds;
      axios.post("/card/cardIds", {cardIds})
      .then(res => {
        console.log("cards fetched", res.data);
        this.setState({
          cards: res.data
        })
        console.log("State ", this.state)

      }).catch((err) => {
        console.log("Card Error", err);
      });
    }

  }

  chooseModule = (e) => {

    this.setState({
      index: e
    }); 
    this.handleSelectModule();
    this.getCards(e);

  }

  handleChange = (e) => {
    this.setState({
      moduleNumber: e.target.value
    })
    console.log('Module Number', this.state.moduleNumber);
    let i = 0;
    for( i; i < this.state.modules.length; i ++){
      if( this.state.modules[i].id ==  e.target.value){
        let x = this.state.modules.indexOf( this.state.modules[i] );
        this.setState({
          index: x
        }); 
        this.getCards(x );


    }
   }
  }

  handleEditMode = (e) => {
    this.setState({
      editMode: !this.state.editMode
    })
    this.getCards(this.state.index);
  }
  handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      console.log('Module Number', this.state.moduleNumber);
    }
  }
    render() {

       return (

              <div>
                <br/>
                <br/>
                <br/>
                <br/>
                <h1>  
                  {this.state.name}

                </h1>

                <Grid container className="card-container">                  
                    <Grid item sm>

                    {
                      this.state.isSelecting && (

                        <div className="module-selector-active" onClick={this.handleSelectModule}>
                          <span>
                          Select Module  
                          </span> 

                        </div>
                      )
                    }

                    {
                      !this.state.isSelecting && (
                        <div className="module-selector" onClick={this.handleSelectModule}>
                          <span>
                          Select Module  
                          </span> 

                      </div>
                      )
                    }

                        <div className="module-selector-num">

                         <form>
                            <input onChange={this.handleChange} onKeyPress={this.handleKeyDown} type="text" id="mnumber" name="mnumber" placeholder="Module Number"/>
                          </form>     

                        </div>

                      {
                        this.state.editMode && (
                          <div className="edit-mode-active" onClick={this.handleEditMode}>
                            <span>
                              Edit Mode  
                            </span> 

                           </div>
                        )
                      }

                      {
                        !this.state.editMode && (
                          <div className="edit-mode" onClick={this.handleEditMode}>
                            <span>
                              Edit Mode  
                            </span> 

                           </div>
                        )
                      }


                        <div className="module-sticker" onClick={this.handleSelectModule}>
                            Module {this.state.loaded && 
                            (
                              <span>
                                {this.state.modules[this.state.index].id}                                                            
                              </span>
                            )} 

                        </div>
                        <div className="module-pop-up">
                          {
                            this.state.isSelecting && (
                              <span>

                                {this.state.modules.map((module, i) => (
                                  <span onClick={() => this.chooseModule(i)} key={i}>
                                    <Module module={module}/>                                                                  
                                  </span>

                                  ))}


                              </span>
                            )
                          }

                        </div>  




                      <ArrowBackIosIcon onClick={this.handleBackwardClick} className="arrow-a"/>
                    </Grid>
                    <Grid item sm>

                      {
                        !this.state.editMode && (

                          this.state.cards.map((card, i) =>(                        
                            <FlashCard card={card} key={i} />                      
                          )  
                          )
                        )
                      }

                      {
                        this.state.editMode && (

                          this.state.cards.map((card, i) =>(                        
                            <FlashCard card={card} key={i} editMode={true} user={this.props.user}/>                      
                          )  
                          )
                        )

                      } 
                    </Grid>  
                      <Grid item sm>

                        { this.state.editMode && (

                          <span>
                            <Typography variant="h2" className={classes.pageTitle}>
                          FlashCard
                        </Typography>

                        <form noValidate>
                          <TextField
                            id="front"
                            name="front"
                            type="text"
                            label="Question"
                            className={classes.textField}
                            value={this.state.front}
                            onChange={this.handleChange}
                            multiline={true}
                            fullWidth
                          />
                          <TextField
                            id="back"
                            name="back"
                            type="text"
                            label="Answer"
                            className={classes.textField}
                            value={this.state.back}
                            onChange={this.handleChange}
                            multiline={true}
                            fullWidth
                          />
                          <Button
                            variant="contained"
                            color="primary"
                            className={classes.button}
                            onClick={this.handleCardSubmit}
                          >
                            Add New Card
                          </Button>

                          <br />
                          </form>

                          </span>

                        )}

                        <ArrowForwardIosIcon onClick={this.handleForwardClick} className="arrow-b"/>
                      </Grid>
                </Grid>
             </div>

        );
    }
}

Content.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Content);
